UPDATE activities SET content = '{"type":"speaking","data":{"phrases":[{"text":"Selamat pagi, kawan-kawan.","translation_ms":"Selamat pagi, kawan-kawan.","translation_zh":"早安，朋友们。","translation_en":"Good morning, friends.","voice_guide":"Ayat ini ialah Selamat pagi, kawan-kawan. Kita guna untuk memberi salam pada waktu pagi kepada rakan-rakan. Sebut dengan saya: Selamat pagi, kawan-kawan."},{"text":"Nama saya {name}.","translation_ms":"Nama saya {name}.","translation_zh":"我的名字是 {name}。","translation_en":"My name is {name}.","voice_guide":"Ayat ini ialah Nama saya. Kita guna untuk memperkenalkan diri kita kepada orang lain. Sebut dengan saya: Nama saya..."},{"text":"Terima kasih, ayah.","translation_ms":"Terima kasih, ayah.","translation_zh":"谢谢爸爸。","translation_en":"Thank you, father.","voice_guide":"Ayat ini ialah Terima kasih, ayah. Kita guna untuk mengucapkan terima kasih kepada ayah. Sebut dengan saya: Terima kasih, ayah."}],"use_kid_name":true}}'::jsonb WHERE title_ms = 'Mari Bercerita - Terima Kasih, Ayah' AND type = 'speaking';

